import { Link } from '@material-ui/core';
import { AddShoppingCart } from '@material-ui/icons';
import React, { useEffect, useMemo, useState } from 'react';
import PaginationComponenet from './PaginationComponenet';

function Products() {
    const [users, setUsers] = useState([]);
    const [count, setCount] = useState(0);
    const [totalItems, setTotalItems] = useState(0);
    const [currentPage, setCurrentPage] = useState();
    const [currentCart,setCart] = useState([]);
    const ITEMS_PER_PAGE = 6;

    const getUsers = async () =>{
        try{
            const response = await fetch('https://api.github.com/users')
            .then((response) => response.json())
            .then(json =>{ setUsers(json)
            }); 
        }catch(error){
            console.log("my error"+error);
        }
    };

    const addToCart = (Elm) =>{ 
        console.log(Elm);
        setCart((currentCart) => [...currentCart, Elm]); }

    useEffect(()=>{
        getUsers();
    },[]);

    const addItem = () => {
        setCount(count + 1)
    }

    const decItem = () => {
        if(count>0) {
            setCount(count-1)
        }
    }

    const usersData = useMemo(() => {
          let computedUsers = users;
          setTotalItems(computedUsers.length)
          //Current page slice
          return computedUsers.slice( 
              (currentPage - 1)* ITEMS_PER_PAGE, 
              (currentPage - 1)*ITEMS_PER_PAGE+ITEMS_PER_PAGE );
    }, [users, currentPage])

  return (
    <div cName='products'>
      <div cName="container">
      <div c="card-columns">
                {
                    usersData.map((Elm)=>{
                        return (
                            <div keys={Elm.id} c="card-body text-center mx-0">
                               <div c="image"><img src={Elm.avatar_url} c="rounded" width="155"/></div>
                               <p c="card-text">Name</p>
                               <p c="card-text">Price ${50}</p>
                               <p c="card-text">item {count}</p>
                               <button c="btn btn-outline-primary mx-1"  onClick={addItem}>+</button>
                               <button c="btn btn-outline-primary mx-1" onClick={decItem}>-</button><br/>
                               <Link to="#"><button c="btn btn-outline-primary mt-1" onClick={()=>addToCart(Elm)}>Add Cart</button></Link>
                            </div>
                        )
                    })
                }
            </div>
            <div cName="pagination">
            <PaginationComponenet 
                    total={totalItems}
                    itemsPerPage={ITEMS_PER_PAGE}
                    currentPage={currentPage}
                    onPageChange={ page => setCurrentPage(page) }
            />
            <button>Go To Cart</button>
            </div>
          </div>
    </div>
  );
}

export default Products;
