import { Link } from '@material-ui/core';
import React from 'react'
import { useEffect } from 'react';
import { useState } from 'react';

const initialValue = [
    {
        offset: 0,
        data: [],
        perPage: 5,
        currentPage: 0
    }
]

const Products = () => {

    const [users, setUsers] = useState([]);
    const [count, setCount] = useState(0);
    const [currentPage, setcurrentPage] = useState(0);
    const [itemsPerPage, setitemsPerPage] = useState(3);
    const [pageNumberLimit, setpageNumberLimit] = useState(5);
    const [maxPageNumberLimit, setmaxPageNumberLimit] = useState(5);
    const [minPageNumberLimit, setminPageNumberLimit] = useState(0);


    const getUsers = async () =>{
        try{
            const response = await fetch('https://api.github.com/users')
            .then((response) => response.json())
            .then((json) => setUsers(json)); 
        }catch(error){
            console.log("my error"+error);
        }   
    }

    useEffect(()=>{
        getUsers();
    },[]);

    const addItem = () => {
        setCount(count + 1)
    }

    const decItem = () => {
        if(count>0){
            setCount(count-1)
        }

    }

    const pages = [];
    for (let i = 1; i <= Math.ceil(users.length / itemsPerPage); i++) {
      pages.push(i);
    }

    const handleClick = (event) => {
        setcurrentPage(Number(event.target.id));
      };

       const renderPageNumbers = pages.map((number) => { 
        if (number < maxPageNumberLimit + 1 && number > minPageNumberLimit) {
          return (
            <li
              key={number}
              id={number}
              onClick={handleClick}
              className={currentPage == number ? "active" : null}
            >
              {number}
            </li>
          );
        } else {
            return null;
        }
      });

      const handleNextbtn = () => {
        setcurrentPage(currentPage + 1);

        if (currentPage + 1 > maxPageNumberLimit) {
          setmaxPageNumberLimit(maxPageNumberLimit + pageNumberLimit);
          setminPageNumberLimit(minPageNumberLimit + pageNumberLimit);
        }
      };

      const handlePrevbtn = () => {
        setcurrentPage(currentPage - 1);

        if ((currentPage - 1) % pageNumberLimit == 0) {
          setmaxPageNumberLimit(maxPageNumberLimit - pageNumberLimit);
          setminPageNumberLimit(minPageNumberLimit - pageNumberLimit);
        }
      };

    return (
        <div className="products">
            <div class="card-columns">
                {
                    users.map((Elm)=>{
                        return (
                            <div keys={Elm.id} class="card-body text-center">
                               <div class="image"><img src={Elm.avatar_url} class="rounded" width="155"/></div>
                               <p class="card-text">Name</p>
                               <p class="card-text">Price</p>
                               <p class="card-text">item {count}</p>
                               <button class="btn btn-outline-primary mx-1"  onClick={addItem}>+</button>
                               <button class="btn btn-outline-primary mx-1" onClick={decItem}>-</button><br/>
                               <Link class="btn btn-primary mx-1 mt-1" to="#">Add Cart</Link>
                            </div>
                        )
                    })
                }
                <ul className="pageNumbers">
                     <li>
                       <button
                         onClick={handlePrevbtn}
                         disabled={currentPage == pages[0] ? true : false}
                       >
                         Prev
                       </button>
                     </li>
                     {renderPageNumbers}
                     <li>
                       <button
                         onClick={handleNextbtn}
                         disabled={currentPage == pages[pages.length - 1] ? true : false}
                       >
                         Next
                       </button>
                     </li>
      </ul>

            </div>
        </div>
    )
    }

export default Products
